import React, { useState, useRef } from 'react';

import './PopUp.scss';

const PopUp = ({ mousePositionX, mousePositionY, deleteMessage, editMessage }) => {

	return (
    <div
			className="wrapper-pop-up"
			style={{ 
				top: `${mousePositionY}+20`, 
				left: mousePositionX, 
			}}
		>
			<div 
				className="pop-up-edit-delete"
				onClick={(e) => editMessage()}
			>
				Редактировать
			</div>

			<div 
				className="pop-up-edit-delete"
				onClick={(e) => deleteMessage()}
			>
				Удалить 
			</div>

		</div>
	);
};

export default PopUp;
