import React, { useRef, useState, useEffect } from 'react';

import Header from './Header/Header'
import Footer from './Footer/Footer'
import Aside from './Aside/Aside'

import './Body.scss';

const Body = () => {
	const [funChatHistory, setFunChatHistory] = useState([]);

	useEffect(() => {
		const localHistory = JSON.parse(localStorage.getItem('funChat'));
		setFunChatHistory(localHistory);
  }, []);

	const addMessage = (e) => {
		if (e.key === 'Enter') {
			e.preventDefault();

			setFunChatHistory([
				...funChatHistory, 
				{
			/* 		date: giveDate(), */
					message: e.target.value,
				},
			]);

			localStorage.setItem('funChat', JSON.stringify(funChatHistory));
		}
	};

	const giveDate = () => {
		let now = new Date();
		return now;
	};

	return (
		<div className="wrapper-main">
			<Aside/>
			<div className="wrapper-header-body">
				<Header/>
				<div className="main-body">
					{funChatHistory.map((item) => (
						<div className="wrapper-all-message">
							<p>{item.message}</p>
						</div>
					))}
				</div>

				<Footer
					addMessage={addMessage}
				/>
			</div>
		</div>
	);
};

export default Body;
