import React, { useRef, useState } from 'react';

import './Footer.scss';

import iconAddFiles from './svg/addFile.svg';
import iconSmile from './svg/smile.svg';
import iconSendMessage from './svg/send.svg';

const [funChatHistory, setFunChatHistory] = useState([]);

const addMessage = (e) => {
	if (e.key === 'Enter') {
		e.preventDefault();
		localStorage.setItem('funChat', e.target.value);
	}
}

const giveDate = () => {
	let now = new Date();
	return now;
}

const Footer = () => {
	return (
    <div className="wrapper-footer">

			<div className="wrapper-icon-footer">
				<img 
					src={iconAddFiles} 
					className="icon-footer" 
					alt="iconAddFiles"
				/>
			</div>

			<div className="wrapper-text-area-message">
				<textarea 
					onKeyDown={(e) => addMessage(e)}
					className="text-area-message"
					placeholder="Write a message"
				>
				</textarea>
			</div>

			<div className="wrapper-icon-footer">
				<img 
					src={iconSmile} 
					className="icon-footer" 
					alt="iconAddFiles"
				/>
			</div>

			<div className="wrapper-icon-footer">
				<img 
					src={iconSendMessage} 
					className="icon-footer" 
					alt="iconAddFiles"
				/>
			</div>

		</div>
	);
};

export default Footer;
