import React, { useState, useRef } from 'react';

import Body from './components/Body/Body';

import './App.css';

const App = () => {
  const [autorizaton, setAutorizaton] = useState(false);
  const loginRef = useRef(null);
  const passwordRef = useRef(null);
  return (
    <section className="main-section">
      {
        !autorizaton &&
        <div className="wrapper-autorization">
          <div className="box-autorization">
            <span className="title-autorization">
              Autorization
            </span>
            <div className="wrapper-login-password">
              <div className="wrapper-login">
                <span>
                  login
                </span>
                <input 
                  ref={loginRef}
                  className="input-login"
                  defaultValue="admin"
                >
                </input>
              </div>
              <div className="wrapper-password">
                <span>
                  password
                </span>
                <input 
                  ref={passwordRef}
                  className="input-password"
                  defaultValue="admin"
                >
                </input>
              </div>
              <button className="autorization">
              login
            </button>
            </div>
          </div>
        </div>
      }
      {
        autorizaton &&
        <Body/>
      }
    </section>
  );
}
export default App;
