import React, { useRef, useState, useEffect } from 'react';

import Header from './Header/Header'
import Footer from './Footer/Footer'
import Aside from './Aside/Aside'
import PopUP from './PopUP/PopUP'

import './Body.scss';

const Body = () => {

	const [mousePositionX , setMousePositionX] = useState(null);
	const [mousePositionY , setMousePositionY] = useState(null);

	const [switchСhat , setSwitchChat] = useState(true);
	const [funChatHistory, setFunChatHistory] = useState([]);
	const [workChatHistory, setWorkChatHistory] = useState([]);
	const [statePopUp, setStatePopUp] = useState(false);

	const mainBodyRef = useRef(null);

	useEffect(() => {
		if (switchСhat) { 
			firstLoadData('workChat', setWorkChatHistory);
		}
			firstLoadData('funChat', setFunChatHistory);
  }, []);

	const firstLoadData = (key, setType ) => {
		let localHistory = JSON.parse(localStorage.getItem(key));
		!!localHistory && setType(localHistory);
	};

	const addMessage = (e) => {
		if (e.key === 'Enter') {
			e.preventDefault();
/* 			mainBodyRef.current.scrollBot = 900; */
			if (switchСhat) { 
				setData(funChatHistory, setFunChatHistory, 'funChat', e);
			} else {
			setData(workChatHistory, setWorkChatHistory, 'workChat', e);
			}
		}
	};

	const setData = (typeChat, setTypeChat, key, e) => {
		setTypeChat([
			...typeChat, 
			{
				date: giveDate(), 
				message: e.target.value,
			},
		]);
		localStorage.setItem(key, JSON.stringify(typeChat));
	};

	const giveDate = () => {
		const date = new Date();
		const hour = date.getHours();
		const min = date.getMinutes();

		return (`${hour}:${min}`);
	};

	const createPopUp = (e) => {
		setMousePositionX(e.nativeEvent.offsetX);
		setMousePositionX(e.nativeEvent.offsetY);
	} 

	return (
		<div className="wrapper-main">
			{
				statePopUp && 
				<PopUP
					mousePositionX={mousePositionX}
					mousePositionY={mousePositionY}
				/>
			}
			<Aside
				setSwitchChat={setSwitchChat}
				switchСhat={switchСhat}
			/>
			<div className="wrapper-header-body">
				<Header/>
				<div 
					className="main-body"
					ref={mainBodyRef}
				>
					<div className="wrapper-main-body">
						{switchСhat && funChatHistory.map((item) => (
							<div className="wrapper-all-message">
								<span>
									{item.message}
								</span>
								<span>
									{item.date}
								</span>
							</div>
						))} 

						{!switchСhat && workChatHistory.map((item) => (
							<div className="wrapper-message">
									<div 
										className="wrapper-message-text"
										onClick={(e) => createPopUp(e)}
									>

										<span className="text-in-message">
											{item.message}
										</span>
										<div className="wrapper-time-in-message">
											<span className="time-in-message">
												{item.date}
											</span>
										</div>
									</div>
							</div>
						))} 
					</div>
				</div>

				<Footer
					addMessage={addMessage}
					switchСhat={switchСhat}
				/>
			</div>
		</div>
	);
};

export default Body;
