import React from 'react';

import './Footer.scss';

import iconAddFiles from './svg/addFile.svg';
import iconSmile from './svg/smile.svg';
import iconSendMessage from './svg/send.svg';

const Footer = ({ addStickerInLocal, addMessage, inputMessage, statePopUpSelectSmile, setStatePopUpSelectSmile, addSticker }) => {
	return (
    <div className="wrapper-footer">

			<div className="wrapper-icon-footer">
				<img 
					src={iconAddFiles} 
					className="icon-footer" 
					alt="iconAddFiles"
				/>
			</div>

			<div className="wrapper-text-area-message">
				<textarea 
					ref={inputMessage}
					onKeyDown={(e) => addMessage(e)}
					className="text-area-message"
					placeholder="Write a message"
				>
				</textarea>
			</div>

			<div className="wrapper-icon-footer">
				<img 
					onClick={() => setStatePopUpSelectSmile(!statePopUpSelectSmile)}
					src={iconSmile} 
					className="icon-footer" 
					alt="iconAddFiles"
				/>
			</div>

			<div className="wrapper-icon-footer">
				<img 
					onMouseDown={(e) => addSticker(e)}
					onMouseUp={(e) => addStickerInLocal(e)}
					src={iconSendMessage} 
					className="icon-footer" 
					alt="iconAddFiles"
				/>
			</div>

		</div>
	);
};

export default Footer;
