import React, { useRef, useState } from 'react';
import Header from './Header/Header'
import Footer from './Footer/Footer'

import './Body.scss';

const Body = () => {
	const [funChatHistory, setFunChatHistory] = useState([]);

	const addMessage = (e) => {
		if (e.key === 'Enter') {
			e.preventDefault();

			setFunChatHistory([
				...funChatHistory, 
				{
					date: giveDate(),
					message: e.target.value,
				},
			]);

			localStorage.setItem('funChat', funChatHistory);
		}
	}

	const giveDate = () => {
		let now = new Date();
		return now;
	}

	return (
		<div className="wrapper-header-body">
			<Header/>

			<div className="main-body">
				{funChatHistory.map((item) => (
					<div className="">

						<p>{item.message}</p>
					</div>
				))}
			</div>

			<Footer
				addMessage={addMessage}
			/>
		</div>
	);
};

export default Body;
