import React, { useState, useRef } from 'react';

import './PopUpSelectSmile.scss';

const PopUpSelectSmile = () => {
	const emojis = [0x1F600, 0x1F604, 0x1F34A, 0x1F344, 0x1F37F, 0x1F363, 0x1F370, 
		0x1F355, 0x1F354, 0x1F35F, 0x1F6C0, 0x1F48E, 0x1F5FA, 0x23F0, 0x1F579, 0x1F4DA,
		0x1F431, 0x1F42A, 0x1F439, 0x1F424];

	return (
		<div className="wrapper-pop-up-smiles">
			{[...Array(20)].map((item, index) => (
							<div 
								className="smile-icon"
								onClick={(e) => addSmile(emojis[index])}
							> 
								{String.fromCodePoint(emojis[index])}
							</div>
						))} 
		</div>
	);
};

export default PopUpSelectSmile;
